mapper.select:
select userid, username, useraddress, userjoindate
from member
where userid = #{userid} and userpwd = #{userpwd}

select userid, userpwd, username, useraddress, userjoindate
from member
where userid = #{userid}

mapper.update:
update member 
   set userpwd = #{userpwd}, 
   	   username = #{username},
   	   useraddress = #{useraddress}
 where userid = #{userid}
