mapper.select:
select member_id, member_name, member_address, member_joindate
from member
where member_id = #{member_id} and member_pw = #{member_pw}

select member_id, member_pw, member_name, member_address, member_joindate
from member
where member_id = #{member_id}

mapper.update:
update member 
   set member_pw = #{member_pw}, 
   	   member_name = #{member_name},
   	   member_address = #{member_address}
 where member_id = #{member_id}
